<?phpif ( ! defined( 'ABSPATH' ) ) exit;/** * Footer Template * * Here we setup all logic and XHTML that is required for the footer section of all screens. * * @package WooFramework * @subpackage Template */	global $woo_options;	$total = 4;	if ( isset( $woo_options['woo_footer_sidebars'] ) && ( $woo_options['woo_footer_sidebars'] != '' ) ) {		$total = $woo_options['woo_footer_sidebars'];	}	if ( ( woo_active_sidebar( 'footer-1' ) ||		   woo_active_sidebar( 'footer-2' ) ||		   woo_active_sidebar( 'footer-3' ) ||		   woo_active_sidebar( 'footer-4' ) ) && $total > 0 ) {?>		<?php woo_footer_before(); ?>	<section class="stripe">    	<div class="col-full">			<p><?php _e( 'Over 250,000 patients helped and counting. Reach out to us for the very best in pain management.', 'rachaelbutts'); ?></p>    	</div>	</section>		<div class="footer-wrap">		<section id="footer-widgets" class="col-full col-<?php echo $total; ?> fix">			<?php $i = 0; while ( $i < $total ) { $i++; ?>				<?php if ( woo_active_sidebar( 'footer-' . $i ) ) { ?>			<div class="block footer-widget-<?php echo $i; ?>">	        	<?php woo_sidebar( 'footer-' . $i ); ?>			</div>		        <?php } ?>			<?php } // End WHILE Loop ?>		</section><!-- /#footer-widgets  -->	<?php } // End IF Statement ?>				<footer id="footer" class="col-full">			<hr />			<div id="copyright" class="col-left">			<?php if( isset( $woo_options['woo_footer_left'] ) && $woo_options['woo_footer_left'] == 'true' ) {					echo stripslashes( $woo_options['woo_footer_left_text'] );			} else { ?>				<p>&copy; <?php echo date( 'Y' ); ?> <?php bloginfo(); ?></p>			<?php } ?>			</div>						<?php				$defaults = array(					'theme_location'  => 'footer-menu',					'menu'            => 'footer-menu',					'container'       => 'div',					'container_class' => 'footer-menu',					'container_id'    => '',					'menu_class'      => 'menu',					'menu_id'         => '',					'echo'            => true,					'fallback_cb'     => 'wp_page_menu',					'before'          => '',					'after'           => '',					'link_before'     => '',					'link_after'      => '',					'items_wrap'      => '<ul id="%1$s" class="%2$s">%3$s</ul>',					'depth'           => 0,					'walker'          => ''				);								wp_nav_menu( $defaults );								?>		</footer><!-- /#footer  -->	</div><!--/.footer-wrap--></div><!-- /#wrapper --><!-- <section class="stripe">&nbsp;</section> --><?php wp_footer(); ?><?php #woo_foot(); ?><!--[if lte IE 8]>	<link rel="stylesheet" type="text/css" href="<?php echo get_stylesheet_directory_uri(); ?>/ie/ie8-and-down.css" /><![endif]--></body></html>